package com.example.model;public class BookInfo {	private int id;	private String ISBN;	private String bookName;	private String author;	private String classifyNumber;		public void setId(int id){		this.id = id;	}	public void setISBN(String ISBN){		this.ISBN = ISBN;	}	public void setBookName(String bookName){		this.bookName = bookName;	}	public void setAuthor(String author){		this.author = author;	}	public void setClassifyNumber(String classifyNumber){		this.classifyNumber = classifyNumber;	}		public int getId(){		return id;	}	public String getISBN(){		return ISBN;	}	public String getBookName(){		return bookName;	}	public String getAuthor(){		return author;	}	public String getClassifyNumber(){		return classifyNumber;	}	}